document.addEventListener('DOMContentLoaded', function () {
  const sidebarToggle = document.querySelectorAll('#sidebarToggle');

  sidebarToggle.forEach(function (toggle) {
    toggle.addEventListener('click', function (e) {
      document.body.classList.toggle('sidebar-toggled');
      document.querySelector('.sidebar').classList.toggle('toggled');

      if (document.querySelector('.sidebar').classList.contains('toggled')) {
        document.querySelectorAll('.sidebar .collapse').forEach(function (collapse) {
          new bootstrap.Collapse(collapse).hide();
        });
      }
    });
  });

  window.addEventListener('resize', function () {
    if (window.innerWidth < 768) {
      document.querySelectorAll('.sidebar .collapse').forEach(function (collapse) {
        new bootstrap.Collapse(collapse).hide();
      });
    }

    if (window.innerWidth < 480 && !document.querySelector('.sidebar').classList.contains('toggled')) {
      document.body.classList.add('sidebar-toggled');
      document.querySelector('.sidebar').classList.add('toggled');
      document.querySelectorAll('.sidebar .collapse').forEach(function (collapse) {
        new bootstrap.Collapse(collapse).hide();
      });
    }
  });

  const sidebar = document.querySelector('body.fixed-nav .sidebar');

  if (sidebar) {
    sidebar.addEventListener('wheel', function (e) {
      if (window.innerWidth >= 768) {
        this.scrollTop += (e.deltaY < 0 ? -30 : 30);
        e.preventDefault();
      }
    });
  }

  document.addEventListener('scroll', function () {
    if (this.scrollTop > 100) {
      document.querySelector('.scroll-to-top').style.display = 'block';
    } else {
      document.querySelector('.scroll-to-top').style.display = 'none';
    }
  });

  document.querySelectorAll('a.scroll-to-top').forEach(function (link) {
    link.addEventListener('click', function (e) {
      e.preventDefault();
      const target = document.querySelector(this.getAttribute('href'));
      window.scrollTo({
        top: target.offsetTop,
        behavior: 'smooth'
      });
    });
  });
});
